package com.phoenix.spaceinvaders.entity.instance;

import java.applet.AudioClip;
import java.awt.Graphics;
import java.awt.Image;

import com.phoenix.spaceinvaders.entity.SIinvader;
import com.phoenix.util.ResourceUtil;

/**
 * Space Invader representing the bottom 2 rows.
 * 
 * @author William
 *
 */
public class SIbottom extends SIinvader
{

    protected final static int defaultValue = ResourceUtil.getInt( SIbottom.class, "value", 10 );

    protected static final Image defaultAlive = ResourceUtil
            .getImage(
                    SIbottom.class,
                    "SIbottom0.gif",
                    "" );

    protected static final Image defaultAliveAlt = ResourceUtil
            .getImage(
                    SIbottom.class,
                    "SIbottom1.gif",
                    "" );

    protected static final Image defaultDestroyed = ResourceUtil
            .getImage(
                    SIbottom.class,
                    "SIinvaderBlast.gif",
                    "" );

    protected static final AudioClip defaultSound = ResourceUtil.getAudio( SIbottom.class, "SIshipMove.wav", "" );

    protected static final AudioClip defaultDestroySound = ResourceUtil
            .getAudio(
                    SIbottom.class,
                    "SIshipHit.wav",
                    "" );

    public SIbottom( int x, int y )
    {
        this( x, y, defaultAlive, defaultAliveAlt, defaultDestroyed, defaultSound, defaultDestroySound, defaultValue );
    }

    public SIbottom( int x, int y, int value )
    {
        this( x, y, defaultAlive, defaultAliveAlt, defaultDestroyed, defaultSound, defaultDestroySound, value );
    }

    public SIbottom( int x, int y, Image alive, Image aliveAlt, Image destroyed, AudioClip sound, AudioClip destroySound, int value )
    {
        this.alive = alive;
        this.aliveAlt = aliveAlt;
        this.destroyed = destroyed;
        xPosition = x;
        yPosition = y;
        this.sound = sound;
        this.destroySound = destroySound;
        this.value = value;
        super.init();
    }

    /**
     * {@inheritDoc}
     */
    @Override
    public void paintComponent( Graphics g )
    {
        if ( sound != null && playSound )
        {
            sound.play();
            sound.loop();
            playSound = false;
        }
        g.drawImage( visual, xPosition, yPosition, null );
    }

}
