package com.phoenix.spaceinvaders.entity.instance;

import java.applet.AudioClip;
import java.awt.Graphics;
import java.awt.Image;

import com.phoenix.spaceinvaders.entity.SIthing;
import com.phoenix.util.ResourceUtil;

/**
 * Simplest of displayable objects, the Missile is a white-filled rectangle.
 * 
 * @author William
 *
 */
public class SImissile extends SIthing
{

    protected static final Image defaultVisual = ResourceUtil
            .getImage(
                    SImissile.class,
                    "missile.gif",
                    "" );

    protected static final AudioClip defaultSound = ResourceUtil
            .getAudio(
                    SImissile.class,
                    "SIbaseShoot.wav",
                    "" );

    public SImissile( int x, int y )
    {
        this( x, y, defaultSound );
    }

    public SImissile( int x, int y, AudioClip sound )
    {
        this( x, y, defaultVisual, sound );
    }

    public SImissile( int x, int y, Image visual )
    {
        this( x, y, visual, defaultSound );
    }

    public SImissile( int x, int y, Image visual, AudioClip sound )
    {
        this.visual = visual;
        init();
        xPosition = x;
        yPosition = y;
        this.sound = sound;
    }

    protected void init()
    {
        playSound = true;
        width = visual.getWidth( null );
        height = visual.getHeight( null );
    }

    /**
     * {@inheritDoc}
     */
    @Override
    public void paintComponent( Graphics g )
    {
        if ( sound != null && playSound )
        {
            // Missile plays it's sound once.
            sound.play();
            playSound = false;
        }
        g.drawImage( visual, xPosition, yPosition, null );

    }
}
