package com.phoenix.spaceinvaders.entity.instance;

import java.applet.AudioClip;
import java.awt.Graphics;
import java.awt.Image;
import java.util.Random;

import com.phoenix.spaceinvaders.entity.SIinvader;
import com.phoenix.util.ResourceUtil;

/**
 * Space Invader representing the mystery ship.
 * 
 * @author William
 *
 */
public class SImystery extends SIinvader
{

    protected final static int defaultValue1 = ResourceUtil.getInt( SImystery.class, "value50", 50 );
    protected final static int defaultValue2 = ResourceUtil.getInt( SImystery.class, "value100", 100 );
    protected final static int defaultValue3 = ResourceUtil.getInt( SImystery.class, "value150", 150 );
    protected final static int defaultValue4 = ResourceUtil.getInt( SImystery.class, "value300", 300 );

    protected static final Image defaultAlive = ResourceUtil
            .getImage(
                    SImystery.class,
                    "SImystery0.gif",
                    "" );

    protected static final Image defaultAliveAlt = ResourceUtil
            .getImage(
                    SImystery.class,
                    "SImystery1.gif",
                    "" );

    protected static final Image defaultDestroyed = ResourceUtil
            .getImage(
                    SImystery.class,
                    "SIinvaderBlast.gif",
                    "" );

    protected static final AudioClip defaultSound = ResourceUtil
            .getAudio(
                    SImystery.class,
                    "SIshipMove.wav",
                    "" );

    protected static final AudioClip defaultDestroyedSound = ResourceUtil
            .getAudio(
                    SImystery.class,
                    "SIshipHit.wav",
                    "" );

    public SImystery( int x, int y, boolean direction )
    {
        this( x, y, direction, defaultAlive, defaultAliveAlt, defaultDestroyed, defaultSound, defaultDestroyedSound );
    }

    public SImystery( int x, int y, boolean direction, int value )
    {
        this( x, y, direction, defaultAlive, defaultAliveAlt, defaultDestroyed, defaultSound, defaultDestroyedSound, value );
    }

    public SImystery( int x, int y, boolean direction, Image alive, Image aliveAlt, Image destroyed, AudioClip sound,
            AudioClip destroyedSound )
    {
        this.alive = alive;
        this.aliveAlt = aliveAlt;
        this.destroyed = destroyed;
        xPosition = x;
        yPosition = y;
        this.direction = direction;
        this.sound = sound;
        switch ( new Random().nextInt( 4 ) )
        {
            case ( 0 ) :
                value = defaultValue1;
                break;
            case ( 1 ) :
                value = defaultValue2;
                break;
            case ( 2 ) :
                value = defaultValue3;
                break;
            default :
                value = defaultValue4;
        }
        init();
    }

    public SImystery( int x, int y, boolean direction, Image alive, Image aliveAlt, Image destroyed, AudioClip sound,
            AudioClip destroyedSound, int value )
    {
        this.alive = alive;
        this.aliveAlt = aliveAlt;
        this.destroyed = destroyed;
        xPosition = x;
        yPosition = y;
        this.direction = direction;
        this.sound = sound;
        this.value = value;
        super.init();

    }

    /**
     * {@inheritDoc}
     */
    @Override
    public void paintComponent( Graphics g )
    {
        if ( sound != null && playSound )
        {
            sound.play();
            sound.loop();
            playSound = false;
        }
        g.drawImage( visual, xPosition, yPosition, null );
    }

}
